import React, { useEffect, useRef } from 'react';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

const Header = ({ toggleSideNav }) => {
    const headerRef = useRef(null);
    const logoRef = useRef(null);

    useEffect(() => {
        const headerElement = headerRef.current;
        const logoElement = logoRef.current;

        // GSAP Animation for header visibility
        gsap.fromTo(headerElement,
            { y: "-100px", opacity: 0 },
            {
                y: 0,
                opacity: 1,
                scrollTrigger: {
                    trigger: ".hero-section",
                    start: "top top",
                    end: "bottom top",
                    scrub: true,
                    markers: false
                }
            }
        );

        // GSAP Animation for logo transition
        gsap.fromTo(logoElement,
            { y: "50vh", scale: 2, x: "50vw", opacity: 1 },
            {
                y: "0",
                scale: 1,
                x: "0",
                opacity: 1,
                scrollTrigger: {
                    trigger: ".hero-section",
                    start: "top top",
                    end: "bottom top",
                    scrub: true,
                    markers: false
                }
            }
        );

    }, []);

    return (
        <header id="header" className="pre-scroll" ref={headerRef}>
            <div className="menu-toggle" onClick={toggleSideNav}>
                <span></span>
                <span></span>
                <span></span>
            </div>
            <div className="header-links">
                <ul>
                    <li><a href="/shop-all">Shop All</a></li>
                    <li><a href="/contact-us">Contact Us</a></li>
                    <li><a href="/track-order">Track your order</a></li>
                </ul>
            </div>
            <div className="header-icons">
                <a href="/search"><img src="/Static/Symbols/magnifying-glass.png" alt="Search" /></a>
                <a href="/cart"><img src="/Static/Symbols/shopping-bag.png" alt="Cart" /></a>
            </div>
            <h1 className="header-title" ref={logoRef}>ATNATIC</h1>
        </header>
    );
};

export default Header;
