import React, { useEffect, forwardRef } from 'react';
import { Link } from 'react-router-dom';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

const Header = forwardRef(({ toggleSideNav }, ref) => {
    useEffect(() => {
        const headerElement = ref.current;

        // Animation for header appearance
        gsap.fromTo(headerElement,
            { y: "-100px", opacity: 0 },
            {
                y: 0,
                opacity: 1,
                scrollTrigger: {
                    trigger: ".hero-section",
                    start: "top top",
                    end: "bottom top",
                    scrub: true,
                    markers: false
                }
            }
        );

        // Animation for ATNATIC text
        gsap.fromTo(".hero-scroll",
            { y: "50vh", opacity: 1 },
            {
                y: "0vh",
                opacity: 0,
                scrollTrigger: {
                    trigger: ".hero-section",
                    start: "top top",
                    end: "bottom top",
                    scrub: true,
                    markers: false
                }
            }
        );

    }, [ref]);

    return (
        <header id="header" className="pre-scroll" ref={ref}>
            <div className="menu-toggle" onClick={toggleSideNav}>
                <span></span>
                <span></span>
                <span></span>
            </div>
            <div className="header-links">
                <ul>
                    <li><Link to="/shop-all">Shop All</Link></li>
                    <li><Link to="/contact-us">Contact Us</Link></li>
                    <li><Link to="/track-order">Track your order</Link></li>
                </ul>
            </div>
            <div className="header-icons">
                <Link to="/search"><img src="/Static/Symbols/magnifying-glass.png" alt="Search" /></Link>
                <Link to="/cart"><img src="/Static/Symbols/shopping-bag.png" alt="Cart" /></Link>
            </div>
            <h1 className="header-title">ATNATIC</h1> {/* Add header-title here */}
        </header>
    );
});

export default Header;
