import React, { useState, useEffect, useRef } from 'react';
import { Route, Routes, useLocation } from 'react-router-dom';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';
import Header from './components/Header';
import SideNav from './components/SideNav';
import HeroSection from './components/HeroSection';
import ProductSection from './components/ProductSection'; 
import ProductPage from './components/ProductPage';

function App() {
    const [isSideNavOpen, setSideNavOpen] = useState(false);
    const [lastScrollTop, setLastScrollTop] = useState(0);
    const headerRef = useRef(null);
    const heroTextRef = useRef(null);
    const location = useLocation();

    const toggleSideNav = () => {
        setSideNavOpen(prevState => !prevState);
    };

    const closeSideNav = () => {
        setSideNavOpen(false);
    };

    const handleScroll = () => {
        const header = headerRef.current;
        const heroText = heroTextRef.current;
        const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
        const threshold = header ? header.offsetHeight : 0;

        if (header) {
            if (scrollTop > threshold) {
                header.classList.add('visible');
                header.classList.remove('pre-scroll');
            } else {
                header.classList.remove('visible');
                header.classList.add('pre-scroll');
            }
        }

        if (heroText) {
            if (scrollTop >= threshold) {
                heroText.classList.add('sticky');

                if (scrollTop < lastScrollTop) {
                    // User is scrolling up
                    heroText.classList.add('scroll-up');
                } else {
                    // User is scrolling down
                    heroText.classList.remove('scroll-up');
                }
            } else {
                heroText.classList.remove('sticky', 'scroll-up');
            }
        }

        setLastScrollTop(scrollTop <= 0 ? 0 : scrollTop); // For Mobile or negative scrolling
    };

    useEffect(() => {
        headerRef.current.classList.add('pre-scroll');
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    useEffect(() => {
        if (location.pathname === '/') {
            window.scrollTo(0, 0);
            handleScroll(); 
        }
    }, [location.pathname]);

    return (
        <div className="App">
            <Header toggleSideNav={toggleSideNav} ref={headerRef} />
            <SideNav isOpen={isSideNavOpen} closeSideNav={closeSideNav} />
            <Routes>
                <Route path="/" element={
                    <>
                        <HeroSection ref={heroTextRef} />
                        <ProductSection />
                    </>
                } />
                <Route path="/product" element={
                    <ProductPage />
                } />
            </Routes>
        </div>
    );
}

export default App;
